class Solution {
public:
    vector<int> addToArrayForm(vector<int>& num, int k) {
        vector<int>v,v1;
        while(k>0){
            v.push_back(k%10);
            k/=10;
        }
        reverse(num.begin(),num.end());
        int carry=0;
        int i=0;
        for( i=0;i<min(v.size(),num.size());i++ ){
          int sum=v[i]+num[i]+carry;
            if(sum>9){
                carry=1;
                v1.push_back(sum%10);
            }else{
                carry=0;
                v1.push_back(sum);
            }
        }
        for(int j=i;j<v.size();j++){
            int sum=v[j]+carry;
            if(sum>9){
                carry=1;
                v1.push_back(sum%10);
            }else{
                carry=0;
                v1.push_back(sum);
