# Code
```
use std::iter::repeat;
impl Solution {
    pub fn add_to_array_form(num: Vec<i32>, k: i32) -> Vec<i32> {
        let mut k = k;
        let digit10 = std::iter::from_fn(move || match k.cmp(&0) {
            std::cmp::Ordering::Greater => {
                let d = k % 10;
                k /= 10;
                Some(d)
            }
            _ => None,
        });
        let mut ret : Vec<i32> = num.into_iter().rev().chain(repeat(-1)).zip(digit10.chain(repeat(-1))).scan(0, |carry, (a, b)|{
            match (a, b, *carry) {
                (-1, -1, 0) => None,
                _ => {
                    *carry += a.max(0) + b.max(0);
                    let res = Some(*carry % 10);
                    *carry /= 10;
                    res
                }
            }
        }).collect();
        ret.reverse();
        ret
    }
}
```

