                list.add(0, sum);
                carry = 0;
            }
        }
        while(temp != 0){
            rem = temp %10;
            sum = carry + rem;
            temp= temp / 10;
            if(sum > 9){
                store = sum %10;
                list.add(0,store);
                carry = 1;
            }
            else{
                list.add(0, sum);
                carry = 0;
            }
        }
        if(carry == 1){
            list.add(0 , 1);
        }
        return list;
    }
}
