class Solution {
public:
    vector<int> addToArrayForm(vector<int>& num1, int k) {
        vector<int>num2;
//      Converting k into vector
        while(k){ 
            num2.push_back(k%10);
            k=k/10;
        }
        reverse(num2.begin(),num2.end());
//      Reverse the vector
        vector<int>vect;
//      First pointer at the end of num1
        int i=num1.size()-1;
//      Second pointer at the end of num2
        int j=num2.size()-1;
        int carry=0;
        while(i>=0 || j>=0 || carry){
//          condition is running untill array has element.
            if(i>=0){
                carry+=num1[i];
                i--;
            }
//          condition is running untill array has element.
            if(j>=0){
                carry+=num2[j];
                j--;
            }
            vect.push_back(carry%10);
            carry=carry/10;
        }
//         reverse the 
        reverse(vect.begin(),vect.end());
        return vect;
    }
};
