            carry=temp/10; // calculating carry
            num[i]=temp%10;
            k=k/10;
        }
        while(k) //if k is greater than num's size 
        {
            int temp=(k%10)+carry;
            num.insert(num.begin(),temp%10);
            carry=temp/10;
            k/=10;
        }
        if(carry >0) num.insert(num.begin(),carry); // if at last carry is left we need to insert it
        // at the beginnnig  
        return num;
    }
};
