class Solution:
    def addToArrayForm(self, A: 'List[int]', K: 'int') -> 'List[int]':
        """
        Method 1, convert A to an integer first, do the addition, finally             convert to list
        """
        def list2int(l):
            s = list(map(str, l))
            s = ''.join(s)
            return int(s)

        def int2list(i):
            s = str(i)
            return list(map(int, s))

        return int2list(list2int(A) + K)

        """
        Method 2, Schoolbook Addition
        """
        for i in range(len(A) - 1, -1, -1):
            # Option 1: vanilla way to deal with remainder and quotient
            # K, A[i] =(A[i] + K) // 10, (A[i] + K) % 10
            # Option 2: use divmod()
            K, A[i] = divmod(A[i] + K, 10)

        # If K still has value, unshift it from head
        return list(map(int, str(K))) + A if K else A

