```
public class Solution {
    public IList<int> AddToArrayForm(int[] A, int K) {
        IList<int> res = new List<int>();
        int sum = 0, carry = 0;

        for(int i = A.Length-1; i >= 0; i--) {
            sum = A[i] + (K % 10) + carry;
            res.Add(sum % 10);
            carry = sum / 10;
            K = K / 10;
        }

        K += carry;
        while(K > 0) {
            res.Add(K % 10);
            K = K / 10;
        }

        return new List<int>(res.Reverse());
    }
}
```

