class Solution {
public:
    typedef struct{
        int32_t x;
        int32_t pow;
    }s_pair;

    int32_t pow(int32_t X, int32_t Y, int32_t &ans){
        ans = 0;
        while (X < Y){
            ans += 1;
            X *= 2;
        }
        return X;
    }

    int brokenCalc(int X, int Y) {
        int32_t ans = 0;

        int32_t temp = pow(X, Y, ans);
        int32_t tp = temp - Y;
        if (X > Y)
            ans = X - Y;
        else{
            int32_t anst = ans;
            int32_t i = 0;
            while (tp > 0 && anst > i){
                ans += (tp & 1 > 0);
                tp >>= 1;
                i += 1;
            }
            ans += tp;
        }
        return ans;
    }
};
```

