 You will get TLE on this .
 ```
 class Solution {
public:
    struct node{
        int val;
        int level;
    };
    int brokenCalc(int X, int Y) {


         set<int> visit;
    if(X==Y)
        return 0;
    if(X>Y)
        return X-Y;

    queue<node> q;
    node n = {X, 0};
    q.push(n);


    while (!q.empty())
    {

        node t = q.front();
        q.pop();


        if (t.val == Y)
            return t.level;


        visit.insert(t.val);


        if (t.val*2 == Y || t.val-1 == Y)
            return t.level+1;


        if (visit.find(t.val*2) == visit.end())
        {
            n.val = t.val*2;
            n.level = t.level+1;
            q.push(n);
        }
        if (t.val-1>=0 && visit.find(t.val-1) == visit.end())
        {
            n.val = t.val-1;
            n.level = t.level+1;
            q.push(n);
        }
    }
        return 0;
    }

};
