```
class Solution {
public:
    int brokenCalc(int startValue, int target) {
        /*
        target = multiplySum - minusSum

		multiplySum = 2^n * startValue
		2^(n - 1) < target/startValue <= 2^n
        => the count of multiply = n

        minusSum = 2^n * t0 + 2^(n-1) * t1 + .... + 2^0 * tn
        ti is the count of minus after i times of multiply
        ti = 1 or 0 (i > 0)
        t0 >= 0, because the maximum multiplier is n
        => count of minus = t0 + (t1 + ... + tn)
                    t0 = minusSum / 2^n
         t1 + ... + tn = bit1count(minusSum & bitmask)
        */

        // Get multiply count
        int multCnt = max(ceil(log2((double)target / startValue)), 0.0);
        // Get sum of minus
        int minusSum = pow(2, multCnt) * startValue - target;
        // Get mask for counting 1-bit
        int bitmask = (1 << multCnt) - 1;
        return multCnt + (minusSum >> multCnt) + __builtin_popcount(minusSum & bitmask);
    }
};

```

