class Solution:
    def heuristic(self, start, end):
        if start > end:
            return start - end
        else:
            steps = 0
            while start <= end:
                start *= 2
                steps += 1
            steps += (start - end)
            return steps

    def neighbor(self, start):
        return [start * 2, start - 1]

    def brokenCalc(self, startValue: int, target: int) -> int:
        # A* finds a path from start to goal.
        # h is the heuristic function. h(n) estimates the cost to reach goal from node n.

        # The set of discovered nodes that may need to be (re-)expanded.
        # Initially, only the start node is known.
        # This is usually implemented as a min-heap or priority queue rather than a hash-set.
        start = startValue
        openSetNodes = set([start])
        openSet = [(0, start)] 
        heapq.heapify(openSet)

        # For node n, cameFrom[n] is the node immediately preceding it on the cheapest path from start to n currently known.
        cameFrom = {}
        # For node n, gScore[n] is the cost of the cheapest path from start to n currently known.
        gScore  = {start: 0}
        # For node n, fScore[n] := gScore[n] + h(n). fScore[n] represents our current best guess as to how short a path from start to finish can be if it goes through n.
        fScore = {start: 0}
        while openSet:
        # This operation can occur in O(Log(N)) time if openSet is a min-heap or a priority queue
            # print(openSet)
            fscore,  current = heapq.heappop(openSet)
            openSetNodes.remove(current)
            if current == target:
                count = 0
                while current != start:
                    count += 1
                    current = cameFrom[current]
                return count

            for neighbor in self.neighbor(current):
                # d(current,neighbor) is the weight of the edge from current to neighbor
                # tentative_gScore is the distance from start to the neighbor through current
                if neighbor <= 0:
                    continue
                tentative_gScore = gScore.get(current, 1000000000) + 1
                if tentative_gScore < gScore.get(neighbor, 1000000000):
                    # This path to neighbor is better than any previous one. Record it!
                    cameFrom[neighbor] = current
                    gScore[neighbor] = tentative_gScore
                    fScore[neighbor] = tentative_gScore + self.heuristic(neighbor, target)
                    if neighbor not in openSetNodes:
                        openSetNodes.add(neighbor)
                        heapq.heappush(openSet, (fScore[neighbor], neighbor))
