// key observation:
// 1. only one ops can affect the 1st bit.
// 2. once 1st bit is settled, only one ops can affect the 2nd bit
// 3. so on ... 

class Solution {
public:
    int minKBitFlips(vector<int>& arr, int k) {
        int n = arr.size();
        vector<int> diff(n + 1);
        int cnt = 0;
        for (int i = 0; i < n; ++ i)
        {
            if (i) diff[i] += diff[i - 1];
            if (arr[i] ^ (diff[i] % 2) == 0 && i + k - 1 < n)
            {
                diff[i] += 1;
                diff[i + k] -= 1;
                cnt ++ ;
            }

            if (arr[i] ^ (diff[i] % 2) == 0) return -1;
        }
        return cnt;
    }
};
