#define IOS ios::sync_with_stdio(0); cin.tie(0); cout.tie(0);
class Solution {
    int ans;
public:
    int solver(vector<int>& a,int k){
        int n=a.size(),i=0,cnt=0;
        int p[n+k+2];
        memset(p,0,sizeof(p));

		// if 1st element is 0 we flip it and store that a flip was made in the prefix function
        if(a[0]==0){
            p[0]++;
            p[k]--;
            cnt++;
        }

        for(i=1;i<n;i++){
			// this tell us inside how many flipped windows ith element has fallen i.e no of times ith element has been flipped
            p[i]+=p[i-1];

			// if element is 1 and has been flipped odd times we need to flip it again to make it 0
            if(a[i]==1){
                if(p[i]&1){
                    p[i]++;
                    cnt++;
					// if choosing a flipping window some part of window falls outside the range then return NOT POSSIBLE
                    if(i+k>n){
                        cnt=INT_MAX;
                        break;
                    }
                    p[i+k]--;
                }
            }
            else{ // similar logic to flip 0->1
                if(p[i]%2==0){
                    p[i]++;
                    cnt++;
                    if(i+k>n){
                        cnt=INT_MAX;
                        break;
                    }
                    p[i+k]--;
                }
            }
        }
        return cnt;
    }

    int minKBitFlips(vector<int>& a, int k) {
        IOS;
		// perform same operation on both original array as well as reversed version of array
        ans=solver(a,k);
        reverse(a.begin(),a.end());
        ans=min(ans,solver(a,k));
        if(ans==INT_MAX)
            return -1;
        return ans;

    }
};
