class Solution {
    func minKBitFlips(_ A: [Int], _ K: Int) -> Int {
        var count = A.count

        // Keeps track of number the flips that affect the element at the current index
        var flipCount = 0
        var res = 0

        // Whether a flip was done at an index
        var isFlippedAt = Array(repeating: 0, count: count)

        for i in 0..<A.count {
            if i >= K {
                // at index i the flip the happened at index i-K does not affect it
                // so we need to remove that flip
                flipCount -= isFlippedAt[i-K]
            } 

            if A[i] == 0 && flipCount % 2 == 1 {
                // if the flip count is odd that means the current bit is flipped
                // and if the current bit is 0 it is flipped to a 1
                // which is what we want
                // so do nothing
                continue
            } else if A[i] == 1 && flipCount % 2 == 0 {
                // if the flip count is 0
                // that means the current element stays the same
                // which is what we want if the current element is 1
                continue
            } else {
                // we get here if the flipCount is odd and current element is 1
                // or if the flipCount is even and the current element is 0
                // in which case 1 turned into a 0 or a 0 stayed as a 0
                // and we need to perform a flip at the current index
                // affecting elements from i to i+K-1

                // NOTE: the flip on effects the K elements starting from the current index
                // no elements before it are affected by this flip

                if (i+K) > count {
                    // we can possibly flip starting at this index
                    return -1
                }

                isFlippedAt[i] = 1
                flipCount += 1
                res += 1
            }
        }

        return res
    }
}

