class Solution {
    public int minKBitFlips(int[] nums, int K) { 
        int ans=0;int sum=0;
        Queue<Integer>queue=new LinkedList<>();
        int startTag[]=new int[nums.length];int endTag[]=new int[nums.length];
        for(int i=0;i<nums.length;i++){
            queue.add(i);
            if(queue.size()<K){
                continue;
            }
            int end=0;
            if(queue.peek()-1>=0){
                end=endTag[queue.peek()-1];
            }
            if((nums[queue.peek()]+sum+startTag[queue.peek()]-end)%2==0)
            {
                ans++;
                while(queue.size()!=0){
