class Solution:
    def minKBitFlips(self, nums, k: int) -> int:
        min_flips = 0  # final result
        norm_mod = True  # if norm_mod is True we treat 1 as 1 and 0 as 0, if False 1 as 0, 0 as 1
        chmod = set()  # set of indexes where to change norm_mod

        for i in range(len(nums) - k + 1):  # do all virtual flipping
            if i in chmod:
                norm_mod = not norm_mod
            if (nums[i] == 0 and norm_mod) or (nums[i] == 1 and not norm_mod):
                min_flips += 1
                chmod.add(i + k)
                norm_mod = not norm_mod

        for i in range(len(nums) - k + 1, len(nums)):  # check if possible
            if i in chmod:
                norm_mod = not norm_mod
            if (nums[i] == 0 and norm_mod) or (nums[i] == 1 and not norm_mod):
                return -1

        return min_flips
