    val pLen: Int = len - K + 1
    val ops: Array[Int] = Array.fill(1 + pLen)(0) // 1 based
    var ans: Int = 0
    if(A(0) == 0) {
      ans += 1
      add(1, 1, ops, pLen)
    }

    for {
      i <- 1 until pLen
    } {
      if((A(i) + rangeSum(Math.max(1, i - K + 2), i, ops, pLen)) % 2 == 0) {
        ans += 1
        add(i + 1, 1, ops, pLen)
      }
    }
    for {
        i <- pLen until len
    } {
        if((A(i) + rangeSum(Math.max(1, i - K + 2), Math.min(i, pLen), ops, pLen)) % 2 == 0) {
            return -1
        }
    }
    return ans
  }

  def add(i: Int, v: Int, arr: Array[Int], len: Int): Unit = {
    var ind: Int = i
    while(ind <= len) {
      arr(ind) += v
      ind += ind & (-ind)
    }
  }

  def queryPreSum(n: Int, arr: Array[Int], len: Int): Int = {
    var ind = n
    var ans: Int = 0
    while(ind > 0) {
      ans += arr(ind)
      ind -= ind & (-ind)
    }
    ans
  }

  def rangeSum(l: Int, r: Int, arr: Array[Int], len: Int): Int = {
    queryPreSum(r, arr, len) - queryPreSum(l - 1, arr, len)
  }
}
```

