class Solution:
    def minKBitFlips(self, A: List[int], K: int) -> int:
        # status: record whether current character has been flipped already or not
        # 0: not flipped
        # 1: flipped
        flipped = 0

        # record whether index i, as the start of substring of length K, has been
        # flipped or not
        # 0: not flipped
        # 1: flipped
        has_flipped = [0] * len(A)

        min_flips = 0

        for idx, num in enumerate(A):
            # get the ACTUAL FLIPPED STATUS for current num by removing the flipped
            # status of values that are out of window
            if idx >= K:
                flipped ^= has_flipped[idx - K]

            # we will need to flip current value iff
            # 1) current value is 1 and it has been flipped (i.e. flipped == 1)
            # 2) current value is 0 and it hasn't been flipped (i.e. flipped == 0)
            # which can be concluded by (flipped ^ num == 0)
            if (flipped ^ num) == 0:
                # in case there is no way for us to flip current value
                if idx + K - 1 >= len(A):
                    return -1

                # flip
                flipped ^= 1
                has_flipped[idx] = 1
                min_flips += 1

        return min_flips
