class Solution {
public:

    int minKBitFlips(vector<int>& nums, int k) {
        int n= nums.size();
        vector<int>dp(n+1,0);
        vector<int>temp(n+1,1);
        int ans =0;
        for(int i = 0;i<=n-k;i++){
             int ind = i-k;
             int prev;
             if(ind<0){
                  prev = 0;
             }else{
                  prev = dp[i-k];
             }
            if(i-1>=0)
             prev = dp[i-1] - prev;
             int val = (prev%2==0) ? temp[i] : 1 - temp[i];
           // cout<<prev<<" "<<i<<endl;
             if(nums[i] != val){
                  ans++;

                if(i-1>=0)
                  dp[i] = dp[i-1] + 1;
                else
                  dp[i] += 1;
             }else{
                if(i-1>=0)
                 dp[i] =dp[i-1];
             }
        }
        for(int i = n-k+1;i<n;i++){
             dp[i] =dp[i-1];
        }
        for(int i = n-k+1;i<n;i++){
            int prev;
         if((i-k)>=0)
              prev = dp[i-1] - dp[i-k];
         else
             prev = dp[i-1];
           //   cout<<prev<<" "<<i<<endl;
             int val = (prev%2==0) ? temp[i] : 1 - temp[i];
             if(nums[i] != val){
                  return -1;
             }
        }
        return ans;
    }
};
