class Solution {
public:
    int minKBitFlips(vector<int>& nums, int k) {
        int high=0,temp,res=0,count;
        queue<int> q;
        while(high<nums.size()){
            if(nums[high]==0){
               q.push(high+k-1);
               temp=k;
               count=1;
               res+=1;
               while(temp){
                   if(nums.size()-high<temp)return -1;
                   if(count%2==1)nums[high]==0?nums[high]=1:nums[high]=0;
                   if(nums[high]==0){
                       temp=k;res++;nums[high]=1;
                       q.push(high+k-1);
                       count++;
                   }
                   temp--;
                   if(q.size()>0 && q.front()==high)count--,q.pop();
                   high++;
               }
            }
            else
            high++;
        }
        return res;
    }
};
