class Solution:
    def minKBitFlips(self, nums: List[int], k: int) -> int:
        N = len(nums)
        ones = 0
        count = 0
        invert_ops = 0
        for i in range(N):
            one = nums[i] == 1
            if i >= k and nums[i - k] < 0:
                nums[i - k] += 2 # restore original array
                invert_ops -= 1
            if invert_ops % 2:
                one = not one
            if not one and i <= N - k:
                count += 1
                one = True
                invert_ops += 1
                nums[i] -= 2 # modify original array
            ones += one        
        return count if ones == N else -1
