class Solution:
    def minKBitFlips(self, nums: List[int], k: int) -> int:
        n = len(nums)
        flips = 0
        if nums[0] == 0:
            for i in range(k):
                nums[i] = 1 - nums[i]
            flips += 1

        diff = [abs(nums[i+1] - nums[i]) for i in range(n-1)]

        for i in range(n-k):
            if diff[i] == 1:
                flips += 1

                if i < n-k-1:
                    diff[i+k] = 1- diff[i+k]


        if 1 in diff[n-k:]:
            return -1
        return flips


