class Solution {
public:
    int minKBitFlips(vector<int>& nums, int k) {
        int j=0;
        int count=0;
        queue<int> q;
        while(j<nums.size()){

            while(q.size() && q.front()<j)  q.pop();
            if(q.size()%2==0 && nums[j]==0){
                if((j+k-1)>=nums.size()) return -1;
                q.push(j+k-1);
                count++;
            }
            else if(q.size()&1 && nums[j]){
                if((j+k-1)>=nums.size()) return -1;
                q.push((j+k-1));
                count++;
            }
            j++;
        }
        return count;
    }
};
