class Solution {
public:
    int minKBitFlips(vector<int>& nums, int k) {
        int ans=0,n=nums.size(),cnt=0;
        queue<int>q; // for storing the index of element which is needed to flip so that we can count the flip in given window size i.e. k
        for(int i=0;i<n;i++){
            if(!q.empty() && i-q.front()>=k){  // if window length is greater than k
                q.pop();
                cnt--;
            }
            if((nums[i]+cnt)%2==0){  // checking the current value of nums[i] to make sure whether current element needs to flip or not
                if(n-i<k) return -1;  // if there is any element needs to flip but the remaining window size is less than k than it will not be possible to make all 1 so we will return -1
                 cnt++;
                 q.push(i);
                 ans++;
            }
        }
        return ans;
    }
};
