impl Solution {
    pub fn min_k_bit_flips(nums: Vec<i32>, k: i32) -> i32 {
        let mut nums = nums;
        let k = k as usize;
        let mut cur = 0;
        let mut res = 0;
        let n = nums.len();

        for i in 0..n {
            if i >= k && nums[i - k] > 1 {
                cur -= 1;
                nums[i - k] -= 2;
            }
            if cur % 2 == nums[i] as usize {
                if i + k > n {
                    return -1;
                }
                nums[i] += 2;
                cur += 1;
                res += 1;
            }
        }
        res
    }
}
