    // TLE, O (n^2)
    public int minKBitFlips(int[] nums, int k) {
        if (k > nums.length) return -1;

        int count = 0;
        for (int i=0; i<nums.length; i++) {
            if (nums[i] == 0) {
                if (i+k > nums.length) return -1;

                count++;
                flipNextK(nums, i, k); 
                // this is problem, we need to update next k elements every time.
            }
        }

        return count;
    }

    private void flipNextK(int[] nums, int index, int k) {
        for (int i=index; i<index+k; i++) {
            nums[i] = nums[i] ^ 1;
        }
    }
