    // Same algorithm as above, but optimized.
    public int minKBitFlips(int[] nums, int k) {
        if (k > nums.length) return -1;

        int count = 0;

        // instead of updating next queue elements, we can add last index of flip int this queue.
        Queue<Integer> flipQueue = new LinkedList<>();

        for (int i=0; i<nums.length; i++) {
            // queue size is even, num[i] remains same
            // queue size is odd, num[i] changes.
            if ((flipQueue.size() & 1) != 0) {
                nums[i] = nums[i] ^ 1;
            }

            if (nums[i] == 0) {
                if (i+k > nums.length) return -1;

                count++;
                flipQueue.add(i+k-1);
            }

            if (!flipQueue.isEmpty() && flipQueue.peek() == i) flipQueue.poll();
        }

        return count;
    }

