class Solution {
    public int minKBitFlips(int[] nums, int k) {
        int n = nums.length;
        Queue<Integer> queue = new LinkedList<>();
        int noFlips = 0;
        for(int i = 0 ; i < n ; ++i){
            if(!queue.isEmpty() && queue.peek() <= i-k){
                queue.poll();
            }
            int val =  nums[i];
            int size = ((queue.size() & 1) == 0 ? 0 : 1);
            //If flip is required we add it to queue
            if((val ^ size) == 0){
                queue.add(i);
                noFlips++;
            }
            //If bit is outside the last window and it required a flip
            if(i >= n-k+1 && ((val^size) == 0)){
                return -1;
            }
        }
        return noFlips;
    }
}
