class Solution {
    typedef long long ll;
public:
    int minKBitFlips(vector<int>& v, int k) {
        map<ll, ll>mp;
        ll res = 0, sz = v.size(), ts = 1; // TimeStamp
        for (ll i = 0;i <= sz - k;++i) {
            bool flip = 0;
            auto it = mp.lower_bound(i);
            if (it != mp.end()) {
                flip = (mp.rbegin()->second - it->second + 1) & 1; // if odd no. of changes commited
            }
            if (flip) {
                v[i] = !v[i];
            }
            if (v[i] == 0) {
                ++res;
                mp[i + k - 1] = ts++; // diff of two timestamps gives the no. of changes commited to a particular index
            }
        }
		in last sz-k indices, no k size subarray can be formed. So they must already be all zero in final form now, that is if there does exist a solution.
        for (ll i = sz - k + 1;i < sz;++i) {
            bool flip = 0;
            auto it = mp.lower_bound(i);
            if (it != mp.end()) {
                flip = (mp.rbegin()->second - it->second + 1) & 1;
            }
            if (flip) {
                v[i] = !v[i];
            }
			// brought to the actual state
            if (v[i] == 0) {
                return -1; //  here goes nothing m.f.
            }
        }
        return res;
    }
};
