class Solution {
public:
    int minKBitFlips(vector<int>& nums, int k) {

        int f = 0;
        int n = nums.size();
        int flips[n+1];
        memset(flips, 0, sizeof(flips));

        int cur = 0; 
		// if you flip a bit 3 times, is as good as fliping it ones. 
		// flip_count % 2 is same as parity of cur variable 

        for(int i=0; i<n; i++) {

			// superimpose multiple segment on one another 
			// ith bit will flip multiple times, let say c times
			// c%2 will decide if we want to flip it or not.
			//  cur^=1 does the same thing
            if(flips[i] == 1) {
                cur^=1;
            }

			// just want to check if ith bit after fliping goes to zero
            if((nums[i]^cur) == 0) {
                f++;
                cur^=1;

                if((i+k) > n) {
                    return -1;
                }

				// last index if the block of size k [i, i+k] is fliped correctly
                flips[i+k] = 1;
            }
        }

        return f;

    }
};
