class Solution {  

    public int minKBitFlips(int[] nums, int k) {
        int minFlipCount = 0;        
        boolean fliped = false;
        boolean[] cancelFlip = new boolean[nums.length];        
        for(int i = 0, len = nums.length; i < len; i++) {
            int n = nums[i];            
            if(n == 1 && fliped || n == 0 && !fliped) {
                minFlipCount++;
                fliped = fliped ? false : true;
                if(i + k - 1 >= len) return -1;                
                cancelFlip[i + k - 1] = true;                
            }            
            if(cancelFlip[i]) fliped = fliped ? false : true;
        }        
        return minFlipCount;
    }
}
