        flips = [0]*len(nums)
        csum = 0

        for left in range(0, len(nums)-k+1):
            if (nums[left] + csum) % 2 == 0:
                flips[left] += 1
                csum += 1
            if left >= k-1:
                csum -= flips[left-k+1]

