class Solution {
public:
    int find(vector<int>& nums, int n, int k, vector<int>& flips, int ind, int change)
    {
        if(ind >= nums.size())
            return 0;

        change += flips[ind];
        change %= 2;

        if(change == 0)
        {
            if(nums[ind] == 1)
                return find(nums, n, k, flips, ind + 1, change);
            else
            {
                if(ind + k > nums.size())
                    return 1e7;

                flips[ind + k ]++;
                return find(nums, n, k, flips, ind + 1, !change) + 1;
            }
        }
        else
        {
            if(nums[ind] == 0)
                return find(nums, n, k, flips, ind + 1, change);
            else
            {
                if(ind + k > nums.size())
                    return 1e7;

                flips[ind + k]++;
                return find(nums, n, k, flips, ind + 1, !change) + 1;
            }
        }
    }
    int minKBitFlips(vector<int>& nums, int k) 
    {
        int n = nums.size();
        vector<int> flips(n + 2 , 0);

        int ans = find(nums, n, k, flips, 0, 0);

        if(ans >= 1e7)
            return -1;

        return ans;
    }
};
