class Solution {
    public int minKBitFlips(int[] nums, int k) {
        return dfs(nums, 0, k);
    }

    private int dfs(int[] nums, int start, int k) {
        int next = nums.length;
        for (int i = start; i < nums.length; i++) {
            if (nums[i] == 0) {
                next = i;
                break;
            }
        }
        if (next == nums.length) {
            return 0;
        }
        if (next + k > nums.length) {
            return -1;
        }
        start = next + k;
        for (int i = next ; i < next + k; i++) {
            nums[i] ^= 1;
            if (nums[i] == 0) {
                start = Math.min(start, i);
            }
        }
        int temp = dfs(nums, start, k);
        return temp == -1 ? -1 : temp + 1;
    }
}
