import bisect 
class Solution:
    def minKBitFlips(self, nums: List[int], k: int) -> int:
        res = 0
        L = []
        for i in range(len(nums)):
            j,n = bisect.bisect_right(L,i),len(L)
            if ((n-j)%2==0 and nums[i]==0) or ((n-j)%2!=0 and nums[i]==1):
                if i+k>len(nums):
                    return -1 
                res+=1
                bisect.insort_left(L,i+k)
            nums[i]=1
        return res 
