func minKBitFlips(nums []int, k int) int {
	minFlips1 := lc995Helper(nums, k)
	reverse(nums)
	minFlips2 := lc995Helper(nums, k)
	if min(minFlips1, minFlips2) == math.MaxInt32 {
		return -1
	}
	return min(minFlips1, minFlips2)
}

func lc995Helper(nums []int, k int) int {
	totalFlips, currFlips := 0, 0
	// flipEndIndexes, inclusive
	flipEndIndexes := make([]int, 0, len(nums))
	currentIndex := 0
	for i, v := range nums {
		if currFlips > 0 {
			// if i > flipEndIndexes[currentIndex], we need decrease currFlips by 1
			if i > flipEndIndexes[currentIndex] {
				currentIndex++
				currFlips--
			}
		}
		if currFlips%2 != 0 {
			// odd flip, 0->1, 1->0
			v = v ^ 1
		}
		if v == 0 {
			// we need flip once
			if i+k-1 >= len(nums) {
				// invalid flip
				return math.MaxInt32
			}
			totalFlips++
			currFlips++
			flipEndIndexes = append(flipEndIndexes, i+k-1)
		}
	}
	return totalFlips
}

func reverse(A []int) {
	for i := 0; i < len(A)/2; i++ {
		A[i], A[len(A)-1-i] = A[len(A)-1-i], A[i]
	}
}

func min(values ...int) int {
	minValue := math.MaxInt64
	for _, v := range values {
		if v < minValue {
			minValue = v
		}
	}
	return minValue
}
