func minKBitFlips(nums []int, k int) int {
    var prefix = make([]int, len(nums) + 1);
    var cnt int = 0;
    var res int = 0;

    for i := 0; i < len(nums); i++ {
        cnt += prefix[i];
        num := (nums[i] + (cnt % 2)) % 2;
        if num == 0 {
            if i + k > len(nums) {return -1;}
            cnt++;
            res++;
            prefix[i + k]--;
        }
    }

    return res;
