            cur += f[x]
            if (A[x] + cur) % 2 == 1:
                continue
            else:
                ans += 1
                cur += 1
                f[x + K] -= 1
        for idx in range(x + 1, n):
            cur += f[idx]
            if (A[idx] + cur) % 2 == 0:
                return -1
        return ans
