    public static int findJudge2(int N, int[][] trust) {

        int[][] adj = new int[N+1][N+1];

        int[] horizontal = new int[N+1];    // Sum of values in row for i
        int[] vertical = new int[N+1];      // Sum of values in col for i

        for (int[] ints : trust) {
            int a = ints[0];
            int b = ints[1];
            horizontal[a] += 1;
            vertical[b] += 1;
            adj[a][b] = 1;
        }

        int judge = -1;

        for(int i = 1 ; i <= N ;i++){
            if(horizontal[i] == 0 && vertical[i] == N-1)
                judge = i;
        }

        return judge;
