class Solution {
public:
    int findJudge(int n, vector<vector<int>>& t) {

        unordered_map<int,int>mp;
        for(auto x:t){
            mp[x[0]]=x[1];
        }
        int ans=-1,c=0;
        // check which element has 0 occurence
        for(int i=1;i<=n;i++){
            if(mp[i]==0)ans=i,c++;
        // if geater than 1 has 0 occurence return -1
        if(c>1)return -1;
        }
        c=0;
       // check if this element has n-1 trusties
        for(auto x:t){
            if(x[1]==ans)c++;
        }
        if(c==n-1)return ans;

        return -1;
    }
};
