class Solution {
public:
    int findJudge(int n, vector<vector<int>>& trust) {
        vector<int> graph[n+1]; // adjacency list

        for(auto v : trust)
            graph[v[0]].push_back(v[1]);    // creating list

        for(int i = 1; i<=n; i++){
