class Solution 
{//calculating the indegree and outdegree of every node and storing it into the hash map
//The judge will have no outdegree and the indegree is equivalent to n-1
    class degree
    {
        int indegree;
        int outdegree;
        degree(int indegree, int outdegree)
        {
            this.indegree= indegree;
            this.outdegree= outdegree;
        }
    }
    public int findJudge(int n, int[][] trust) 
    {
        if(trust.length == 0 && n == 1)
            return 1;

        HashMap<Integer, degree> map= new HashMap<>();//Node && indegree, outdegree

        for(int []relationship: trust)
        {
            int from= relationship[0];
            int to= relationship[1];

            map.putIfAbsent(to, new degree(0, 0));
            map.putIfAbsent(from, new degree(0, 0));

            degree degreefrom= map.get(from);
            degree degreeto= map.get(to);

            map.put(to, new degree(degreeto.indegree+1, degreeto.outdegree));
            map.put(from, new degree(degreefrom.indegree, degreefrom.outdegree+1));
        }

        for(Map.Entry<Integer, degree> entry: map.entrySet())
        {
            int indeg= entry.getValue().indegree;
            int outdeg= entry.getValue().outdegree;

            if(indeg == n-1 && outdeg == 0)
                return entry.getKey();
        }

        return -1;
    }
}//please do upvote, it helps a lot
