class Solution:
    def findJudge(self, N: int, trust: List[List[int]]) -> int:
        if N == 1:
            return 1

        folks = [0] * (N + 1)
        judge = 0
        for src, dst in trust:
            folks[src] -= 1
            folks[dst] += 1
            if folks[dst] > folks[judge]:
                judge = dst

        return judge if folks[judge] == N - 1 else -1
