public class Solution {
    public int FindJudge(int N, int[][] trust) {

        // if there is only 1 person (N=1) or trust is empty, then
        // the person is the town judge.
        if (N==1 || trust.Length == 0) return 1;

        int[] truster = new int[N+1];
        int[] trustee = new int[N+1];

        for (int i=0; i<trust.Length; i++ ){            

            // plus1 to trust[i][1] index of truster and trustee
            truster[trust[i][0]]++;
            trustee[trust[i][1]]++;

        }

        // to satify to be the town judge:
        // trustee of index j must be total number of people(N)-1
        // truster of index j must be 0
        for (int j=0; j<trustee.Length; j++){
            if (trustee[j] == N-1 && truster[j] == 0) return j;
        }

        return -1;

    }
}
