class Solution {
public:
    int findJudge(int n, vector<vector<int>>& trust) {

        // base cases
        if(n==1)
        return n;

        if(trust.size()==0)
        return -1;

        vector<int>trusted(n+1);

        for(auto x:trust)
        {
            // increment person who is trusted by everyone
            trusted[x[1]]++;

            // decrement person who is not trusted by everyone
            trusted[x[0]]--;
        }

        for(int i=1;i<=n;i++)
        {
            // return person who only trusts himself & no one else 
            if(trusted[i]==n-1)
            return i;
        }
        return -1;
    }
};
```

