class Solution {
    public int findJudge(int n, int[][] trust) {

    if(trust.length==0&&n==1)return 1;
    else if(trust.length==0)return -1;

    HashMap<Integer,Boolean>count=new HashMap<>();
    HashMap<Integer,Integer>know=new HashMap<>();

    for(int i=0;i<trust.length;i++){
        count.put(trust[i][0],true);
        if(!know.containsKey(trust[i][1])){
            know.put(trust[i][1],1);
        }
        else{
            know.put(trust[i][1],know.get(trust[i][1])+1);
        }
    }

    for(int i=1;i<=n;i++){
        if(!count.containsKey(i)&&know.containsKey(i)&&know.get(i)==n-1){
            return i;
        }
    }

    return -1;

    }
}
```
