    //Taking input in adjacency list. a -> b ----> adj[a] = [b]
    vector<int> adj[1001];
    int findJudge(int n, vector<vector<int>>& trust) 
    {
        for(auto a : trust)
        {
            adj[a[0]].push_back(a[1]);
        }

        //Interating from 1 to N to check who is not trusting to anybody
        //which implies adj[i] should be empty.

        for(int i=1; i<=n; i++)
        {
            // checking adj[i] is empty or not.
            if(adj[i].size() == 0)
            {

                //Now, we to ensure that everybody trust this judge except self.

                bool flag = 0;
                for(int j=1; j<=n; j++)
                {
                    // adj[i] is a vector. So, we are copying in other vector.
                    vector<int> a = adj[j];

                    //sorting is required to apply binary search.
                    sort(a.begin(), a.end());

                    // Now check that this judge ( i ) is present in all others adjacency list except their own (i != j ).
                    // If not present we break the loop and change the flag value.
                    if(j!=i && !binary_search(a.begin(), a.end(), i))
                    {
                        flag = 1;
                        break;
                    }
                }

                // This ensures the judge (i) is present in all except and returns the value.
                if(!flag)
                {
                    return i;
                }
            }
        }

        //returning -1 when no judge is present in the town.
        return -1;
    }
