import java.util.HashMap;
import java.util.Map;
import java.util.Optional;

class Solution {
    public int findJudge(int n, int[][] trust) {
        if(n == 1) return 1;
        Map<Integer, Integer> map = new HashMap<>();
        boolean[] isntTownJudge = new boolean[n + 1];
        for(int[] e: trust) {
            isntTownJudge[e[0]] = true;
            map.put(e[1], map.getOrDefault(e[1], 0) + 1);
        }
        Optional<Map.Entry<Integer, Integer>> judge = map.entrySet().stream().filter(item -> item.getValue() == (n - 1) && !isntTownJudge[item.getKey()]).findFirst();
        return judge.isPresent() ? judge.get().getKey() : -1;

    }
}
```

