impl Solution {
    pub fn find_judge(n: i32, trust: Vec<Vec<i32>>) -> i32 {
        let mut outbound = vec![0; n as usize];
        let mut inbound = vec![0; n as usize];

        for v in trust.iter() {
            outbound[(v[0] - 1) as usize] += 1;
            inbound[(v[1] - 1) as usize] += 1;
        }

        for i in 0..n {
            if inbound[i as usize] == (n - 1) as usize
                && outbound[i as usize] == 0 {
                return i + 1;
            }
        }

        -1
    }
}
