class Solution {
    // solving usig array soln
    // can be done using indegree and outdegree, more intuitive soln
    // the judge wont have an outdegree and indegree should be n-1, then judge is found
    public int findJudge(int n, int[][] trust) {
        int count []= new int[n+1];
        if(trust.length==0 && n==1)
            return 1;

        for(int i =0;i<trust.length;i++)
        {
            int [] arr = trust[i];

            // the person who trusts will be given -1 and the person on which the trust is happening will get +1
            // so if there is a judge he will have count as n-1 as he trusts nobody and there will be always +1 for him
            // in every situation,if the so called judge trusts somebody he wont ever reach n-1 count value 

            count[arr[0]]-=1;
            count[arr[1]]+=1;
        }
        // now check which value satisfies the condn
        for(int i=0;i<count.length;i++)
        {
            if(count[i]==n-1)
                return i;

        }

        return -1;

    }
}

